import {useState} from 'react'; 
import './App.css';

function App() {
  const[meaning, setmeaning]= useState['']; 
  const emojiDic = {
    'üôÇ': "smiling",
    'üôÑ':'eyes roll',
    'ü´£':'shy',
    'üò°':'angry',
    'ü´°':'salute',
    'ü§ó':'hug',
    'üòµ‚Äçüí´':'confused',
    'ü§í':'fever',
    'üíÄ':'dead',
    'üëΩ':'alien'
  }

function emojiHandler(event){
  var userInput = event.target.value;
  var meaning = emojiDic[userInput];
  setmeaning(meaning);
}

  return (
    <div className="App">
     <input onChange= {emojiHandler}/>
     <h2>{meaning}</h2>

    </div>
  );
}

export default App;
