import './App.css';
import { useState } from 'react';

var userName = "User";
var colorOne = "aliceblue";
var colorTwo= "red";

const emojiDictionary= {
  "üòÇ": "laughing",
  "üôÑ": "rolling eyes",
  "üî•": "fire", 
 "üòç" : "heart eyes",
 'üôÇ': "smiling",
 'ü´£':'shy',
 'üò°':'angry',
 'ü´°':'salute',
 'ü§ó':'hug',
 'üòµ‚Äçüí´':'confused',
 'ü§í':'fever',
 'üíÄ':'dead',
 'üëΩ':'alien',
 "üñïüèª":"middle finger",
};
function App() {

  var[meaning, setmeaning] = useState("");

  function inputHandler(event){
    // console.log(event.target.value);
    var userInput = event.target.value; 
    var meaning = emojiDictionary[userInput];

    if(meaning){
     setmeaning(meaning);
    }

    else if(event.target.value=""){
      meaning = "enter a emoji First"
    }
  else{
    meaning = "Not available in the database";
    setmeaning(meaning);

  }

  }

  return (
    // Displaying username and welcoming user
    <div className="App">
      <h1 style={{color:colorOne}}>Welcome <span style={{color:colorTwo}}>{userName}</span></h1>

     <input type="text" placeholder='Enter a emoji' onChange={inputHandler} />
     <h2>{meaning}</h2>
    </div>
  );
}


export default App;
