import {useState} from 'react'; 
import './App.css';

function App() {
  const[meaning, setmeaning]= useState['']; 
  const emojiDic = {
    '🙂': "smiling",
    '🙄':'eyes roll',
    '🫣':'shy',
    '😡':'angry',
    '🫡':'salute',
    '🤗':'hug',
    '😵‍💫':'confused',
    '🤒':'fever',
    '💀':'dead',
    '👽':'alien'
  }

function emojiHandler(event){
  var userInput = event.target.value;
  var meaning = emojiDic[userInput];
  setmeaning(meaning);
}

  return (
    <div>
     <input onChange= {emojiHandler}/>
     <h2>{meaning}</h2>

    </div>
  );
}

export default App;
